        public void perlinMap(int terrainType, float factor, string terrainStuff, float[] fSeed)
        {
       
            Random seed = new Random();
      

            for (int y = 2; y < 20 - 1; y++)
            {
                for (int x = 2; x < 54 - 1; x++)
                {
                    float fNoise = 1.5f;
                    float fScale = 2f + factor*(float)terrainType/2;
                    if (terrainType == 0) { fNoise =  5.5f + seed.Next(1,18); fScale = 12f; }
                    else {  fNoise = 3.5f + terrainType / yCoordinates*factor;}
                       
                    float fScaleAcc = factor * xCoordinates/4;

                    if (mapArray[y, x] == "⁃")  // ####              
                    {

                        int nSeed = seed.Next(1, 4);
                        int nPitch = 52 >> nSeed;
                        int nSampleX1 = (x / nPitch) * nPitch;
                        int nSampleY1 = (y / nPitch) * nPitch;

                        int nSampleX2 = (nSampleX1 + nPitch) % 15;
                        int nSampleY2 = (nSampleY1 + nPitch) % 5;

                        float fBlendX = (x - nSampleX1) / (float)nPitch;
                        float fBlendY = (y - nSampleY1) / (float)nPitch;

                       
                        fScaleAcc += fScale;
                        fNoise += (fBlendY * (nSampleX1 - nSampleX2) + nSampleY1) * fScale - fSeed[nSeed];
                        fNoise = fNoise/25;


