        public int arrayWidth;
        public int arrayHeight;


        public Map()
        {
            int screenWidth = Console.WindowWidth;
            int screenHeight = Console.WindowHeight;

            mapArray = map1Array;

            //2D Array Breite
            arrayWidth = mapArray.GetLength(1);
            //2D Array Höhe
            arrayHeight = mapArray.GetLength(0);

        }

        public void DrawMap()
        {
            Console.OutputEncoding = System.Text.Encoding.UTF8;


            for (int x = 0; x < arrayWidth; x++)
            {
                for (int y = 0; y < arrayHeight; y++)
                {
                    Console.SetCursorPosition(x, y);
                    //find out if this is water..

                    // portals  ▼ ► ◄ ▲

                    if (mapArray[y, x] == "♥")
                    {
                        Console.ForegroundColor = ConsoleColor.Red; // make portals magenta
                    }
                    else if (mapArray[y, x] == "▼")
                    {
                        Console.ForegroundColor = ConsoleColor.DarkYellow; // make portals magenta
                    }
                    else if (mapArray[y, x] == "►")
                    {
                        Console.ForegroundColor = ConsoleColor.Green; // make portals magenta
                    }
                    else if (mapArray[y, x] == "◄")
                    {
                        Console.ForegroundColor = ConsoleColor.DarkCyan; // make portals magenta
                    }
                    else if (mapArray[y, x] == "▲")
                    {
                        Console.ForegroundColor = ConsoleColor.Red; // make portals magenta
                    }
                    else if (mapArray[y, x] == "▒")
                    {
                        Console.ForegroundColor = ConsoleColor.DarkCyan; // make portals magenta
                    }
                    else if (mapArray[y, x] == "●")
                    {
                        Console.ForegroundColor = ConsoleColor.DarkGreen; // make portals magenta
                    }
                    else if (mapArray[y, x] == "▀")
                    {
                        Console.ForegroundColor = ConsoleColor.DarkGray; // make portals magenta
                    }
                    else if (mapArray[y, x] == "☻")
                       {
                 //         Console.ForegroundColor = ConsoleColor.DarkCyan; // make portals magenta
                       }
                    else if (mapArray[y, x] == "♣") // trees
                    {
                        Console.ForegroundColor = ConsoleColor.DarkGreen; // make portals magenta    sun ☼ star ✶  ✸
                    }



                    else
                    {
                        Console.ForegroundColor = ConsoleColor.White;
                    }
                    //2D Array Index: y zuerst
                    Console.Write(mapArray[y, x]);
                }
            }
        }



    

